import './index.scss';

const cats = [

    { "name": "Все" },
    { "name": "Море" },
    { "name": "Горы" },
    { "name": "Архитектура" },
    { "name": "Города" }

]

function App() {
  const [categoryId, setCategoryId]= useState(0)
  const [collections, setCollections]=useState([]);
  const [searchValue, setSearchValue]=useState(' ');


useEffect(()=>{
  fetch('https://6340567dd1fcddf69cb6e217.mockapi.io/photocollection')
.then((res)=> res.json())
.then((json)=> {
  setCollections(json);
})
.catch((err)=> {
  console.warn(err);
  alert('Ошибка при получении данных');
});
}, []);



  return (
    <div className="App">
      <h1>Моя коллекция фотографий</h1>
      <div className="top">
        <ul className="tags">
         {
          cats.map((obj)=>(
          <li className={categoryId === i ? 'active' : ' '} key={obj.name}>{obj.name}</li>
          )
         )}
        </ul>
        <input searchValue={searchValue} onChange={e=> setSearchValue(e.target.value)} className="search-input" placeholder="Поиск по названию" />
      </div>
      <div className="content">
        {
          collections
          .filter(obj=> obj.name.toLowerCase().includes(searchValue.toLowerCase()))
          .map((obj)=> (
            <Collection
          key={obj.id}
          name={obj.name}
          images={obj.photos}
        />
          ))
        }
      </div>
      <ul className="pagination">
        <li>1</li>
        <li className="active">2</li>
        <li>3</li>
      </ul>
    </div>
  );
}

export default App;
